        # Nuestro objeto es válido, podemos proceder
        SET vItems = JSON_LENGTH(pParametroJson);
        
        #Select vItems, pParametroJson;
        
        # El objeto es válido y contiene al menos un elemento
        IF vItems > 0 THEN 
