				select ID into vId_LibroDiario from librodiario where Id_CuentaBancaria = pId_CuentaBancaria and FechaDesde = vCampo2 and FechaHasta = vCampo3;	
			ELSE
				UPDATE `bankcolition`.`cartola`
					SET
					`TotalMovimientos` = vItems,
